pi = 3.141592653589793
e = 2.718281828459045
lemniscate = 2.22057554292119
gauss = lemniscate / pi
euler_mascheroni = 0.577215664901533
sqrt_2 = 2**0.5
sqrt_3 = 3**0.5
sqrt_5 = 5**0.5
golden_ratio = (1 + sqrt_5) / 2
silver_ratio = 1 + sqrt_2
gelfond = e**pi
gelfond_schneider = 2**sqrt_2

def metallic_ratio(n):
    return (n + (n**2 + 4)**0.5) / 2