template:
// URN: {urn}
import {{ test, expect }} from '@playwright/test';

test('{test_name}', async ({{ page }}) => {{
  // Given
  await page.goto('{url}');
  {given_setup}

  // When
  await page.waitForLoadState('networkidle');
  {when_action}

  // Then
  await expect(page).toHaveScreenshot('{snapshot_name}.png', {{
    threshold: {threshold}
  }});
}});
