template:
// URN: {urn}
import http from 'k6/http';
import {{ check, sleep }} from 'k6';

export const options = {{
  vus: {vus},
  duration: '{duration}',
  thresholds: {{
    http_req_duration: ['p(95)<{threshold_ms}'],
  }},
}};

export default function() {{
  // Given
  {given_setup}

  // When
  const response = http.{method}('{url}', {params});

  // Then
  check(response, {{
    'status is {expected_status}': (r) => r.status === {expected_status},
    {additional_checks}
  }});

  sleep(1);
}}
