"""ROI visualization configuration.

This module defines colors and styling for ROI rectangles displayed on kymograph images.
Colors are easily configurable by modifying the constants below.
"""

# ROI rectangle colors
ROI_COLOR_DEFAULT = "blue"  # Color for unselected ROIs
ROI_COLOR_SELECTED = "yellow"  # Color for selected ROI

# ROI rectangle styling
ROI_LINE_WIDTH = 5  # Stroke width for ROI rectangles
ROI_FILL_OPACITY = 0.33  # Fill opacity for ROI rectangles (0.0 = transparent, 1.0 = opaque)

