"""Template for nspec.mk Makefile fragment."""

TEMPLATE = """\
# nspec.mk â€” Generated by nspec init
# Add to your Makefile: include nspec.mk

NSPEC := {runner_prefix}

nspec.validate:
\t$(NSPEC) --validate

nspec.generate:
\t$(NSPEC) --generate

nspec.dashboard:
\t$(NSPEC) --dashboard

nspec.tui:
\t$(NSPEC) --tui

nspec.stats:
\t$(NSPEC) --stats

nspec.check: nspec.validate nspec.generate
"""


def render(*, runner_prefix: str = "nspec") -> str:
    """Return nspec.mk content with the given runner prefix."""
    return TEMPLATE.format(runner_prefix=runner_prefix)
