"""Miscellaneous constants supporting the development of sprites."""

import string

TOTAL_SPRITES: int = 0x400

UNCOMPRESSED_GFX_START = 0x280000
UNCOMPRESSED_GFX_END = 0x330000
# UNCOMPRESSED_GFX_END = 0x333720

SPRITE_PTRS_START = 0x250000
SPRITE_PTRS_END = 0x251000

IMAGE_PTRS_START = 0x251800
IMAGE_PTRS_END = 0x252000

ANIMATION_PTRS_START = 0x252000
ANIMATION_PTRS_END = 0x252C00

ANIMATION_DATA_BANK_1_START = 0x259000
ANIMATION_DATA_BANK_1_END = 0x280000

ANIMATION_DATA_BANK_2_START = 0x360000
ANIMATION_DATA_BANK_2_END = 0x370000

# Common palette constants
PALETTE_SIZE = 30  # 15 colors x 2 bytes each
COLORS_PER_PALETTE = 15

# Sprite palettes (object palettes)
SPRITE_PALETTE_OFFSET = 0x253000
SPRITE_PALETTE_END = 0x259000  # 0x253000 + (819 * 30) = 0x258FE6, rounded up
TOTAL_SPRITE_PALETTES = 819

# Event palettes (0x37A000 - 0x37BFFF per ROM documentation)
EVENT_PALETTE_OFFSET = 0x37A000
EVENT_PALETTE_END = 0x37C000
TOTAL_EVENT_PALETTES = (EVENT_PALETTE_END - EVENT_PALETTE_OFFSET) // PALETTE_SIZE  # 273

# Legacy aliases for backwards compatibility
PALETTE_OFFSET = SPRITE_PALETTE_OFFSET
PALETTE_END = SPRITE_PALETTE_END
TOTAL_PALETTES = TOTAL_SPRITE_PALETTES

DEBUG_INDEX = 3

ALPHABET = string.ascii_lowercase + string.digits

TOTAL_IMAGES = 512
TOTAL_ANIMATIONS = 444

MAX_MOLDS_PER_SPRITE = 32
MAX_SEQUENCES_PER_SPRITE = 16
